INSERT INTO `field_didyouknow` (`pages_id`, `data`, `data1019`, `data1020`) VALUES('3188', 'Poseen un plumaje invernal más apagado, el color de la garganta se torna blanquecino, el gris de la cabeza y partes superiores van de un color grisáceo a café y la banda negra característica del pico casi desaparece. Además es el único de los zambullidores con no tiene penacho en la cabeza.', '', '');
