INSERT INTO `field_didyouknow` (`pages_id`, `data`, `data1019`, `data1020`) VALUES('3306', 'El Loro tricahue antiguamente se distribuía desde la Región de Atacama hasta la Región de Los Ríos pero con el, paso de los años, se ha ido fragmentando su hábitat y restringiendo a dos núcleos poblacionales aislados en nuestro país.', '', '');
