INSERT INTO `field_didyouknow` (`pages_id`, `data`, `data1019`, `data1020`) VALUES('3454', 'En la zona centro de Chile, existe una ciudad llamada San Vicente de Tagua Tagua y se dice que su nombre se debe a que ahí existía una laguna en donde habitaban cientos de estas simpáticas aves.', '', '');
INSERT INTO `field_didyouknow` (`pages_id`, `data`, `data1019`, `data1020`) VALUES('3456', 'Esta elegante ave es endémica del cono sur de Latinoamérica, por lo tanto, no se puede ver en ninguna otra parte del mundo.', '', '');
