INSERT INTO `field_didyouknow` (`pages_id`, `data`, `data1019`, `data1020`) VALUES('3462', 'No existe en Chile otra especie de ave con una cola tan característica como la que posee el Colilarga. De los 24 cm. de su largo total, cerca de 16 cm. corresponden al largo de dos sus rectrices centrales, y 8 cm. al largo de su cuerpo.', '', '');
