INSERT INTO `field_didyouknow` (`pages_id`, `data`, `data1019`, `data1020`) VALUES('3499', 'Los animales carroñeros cumplen un rol fundamental en el ecosistema alimentándose de cuerpos en descomposición. Es más, el nombre de su familia (Cathartidae) proviene del griego “Kathartes”, cuyo significado es “el que limpia”.', '', '');
